import React, { useEffect, useState } from "react";
import axios from "axios";

const TodosContext = React.createContext({
  todos: [],
 fetchTodos: () => {} 
});

export default function GetRegistros() {

  const [Registros, setData] = useState([]);

  useEffect(() => {
    getData("http://localhost:8000/registros").then(setData(Registros))
  },[])


  async function getData(url = "") {
    // Default options are marked with *
    const response = await fetch(url,{
      method: "GET", // *GET, POST, PUT, DELETE, etc.
      mode: "cors", // no-cors, *cors, same-origin
      cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
      credentials: "same-origin", // include, *same-origin, omit
      headers: {
        "Content-Type": "application/json",
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify(data), // body data type must match "Content-Type" header
    });
    console.log(response)
    return response.json(); 
  }


  return (
    <TodosContext.Provider value={{Registros,setData}}>
        {Registros.map(({ id_registro, registro_descripcion }) => (
            <option key={id_registro} value={id_registro}> {registro_descripcion} </option>
          ))}
    </TodosContext.Provider>
  )
}
