import React, { useEffect, useState } from "react";
import axios from "axios";

const TodosContext = React.createContext({
  Registros: [],
  GetRegistros: () => {}
});

export default function GetRegistros() {
  const [Registros, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  useEffect(() => {
    axios("http://localhost:8000/registros")
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
        setError(error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);
  if (loading) return "Loading...";
  if (error) return "Error!";
  return (
    <TodosContext.Provider value={{ Registros, GetRegistros }}>
        {Registros.map(({ id_registro, registro_descripcion }) => (
            <option key={id_registro} value={id_registro}> {registro_descripcion} </option>
          ))}
    </TodosContext.Provider>
  );
}
