import React, { useEffect, useState } from "react";
import axios from "axios";

const TodosContext = React.createContext({
  Actos: [],
  GetActos: () => {},
});

export default function GetActos() {
  const [Actos, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  useEffect(() => {
    axios("http://localhost:8000/actos", {
      params: {
        idActo: -1
      },
    })
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
        setError(error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);
  if (loading) return "Loading...";
  if (error) return "Error!";
  return (
    <TodosContext.Provider value={{ Actos, GetActos }}>
      {Actos.map(({ id_acto, acto_descripcion }) => (
        <option key={id_acto} value={id_acto}>
          {acto_descripcion}
        </option>
      ))}
    </TodosContext.Provider>
  );
}
