import React, { useEffect, useState } from "react";
import axios from "axios";

const TodosContext = React.createContext({
  Registros: [],
  fetchTodos: () => {}
});

export default function GetRegistros() {

  const [Registros, setTodos] = useState(null);

  useEffect(() => {
    fetchTodos()
  }, [])

  const fetchTodos = async () => {
    const response = await fetch("http://localhost:8000/registros")
    const todos = await response.json()
    setTodos(todos.data)


    return (
      <TodosContext.Provider value={{Registros, fetchTodos }}>
          {Registros && Registros.map(({ id_registro, registro_descripcion }) => (
              <option key={id_registro} value={id_registro}> {registro_descripcion} </option>
            ))}
      </TodosContext.Provider>
    );
  }
}
